        <?php
        // If trip button is pressed
        if(isset($_GET['trip'])){
            echo '<form class="black-card margin-top" action="purchase.php" method="post">';
                // Get Price
                $query = "SELECT * FROM routes WHERE route_origin='$origin' AND route_destination='$destination'";
                $result = mysqli_query($conn,$query);
                $row = mysqli_fetch_assoc($result);
                $price = $row['route_price'];
                // Trip
                $trip=$_GET['trip'];
                if($trip=="oneway"){$ticket=1;}else{$ticket=2;}
                // Calculate
                $totalPrice=$price*$passenger*$ticket;
                $totalPrice=number_format($totalPrice, 2,'.',',');
                //if user loggedin
                if(!isset($_SESSION['user_id'])){
                    echo '<div class="price-container">';
                        echo '<div class="price"><span>RM '.$totalPrice.'</span></div>';
                    echo '</div>';
                    echo '<br><p>Please <a href="#06">login</a> to buy a ticket with a given discount!</p>';
                }else{
                    // Get discount
                    if(isset($_SESSION['user_id'])){$user_type=$_SESSION['user_type'];}
                    if($user_type=="admin"){$discount=100;}
                    elseif($user_type=="student"){$discount=50;}
                    elseif($user_type=="disabled"){$discount=40;}
                    elseif($user_type=="senior"){$discount=30;}
                    else{$discount=10;}
                    // Calculate discount
                    $discountPrice=$totalPrice-($price*($discount/100));
                    $discountPrice=number_format($discountPrice, 2,'.',',');
                    echo '<div class="price-container">';
                        echo '<div class="price"><strike><span>RM '.$totalPrice.'</span></strike></div>';
                        echo '<div class="price-after">RM '.$discountPrice.'</div>';
                        echo '<div class="discount-general">-'.$discount.'% OFF<br>'.$user_type.' price</div>';
                    echo '</div>';
                    echo '<br>';
                    echo '<input type="text" name="origin" value="'.$origin.'" hidden>';
                    echo '<input type="text" name="destination" value="'.$destination.'" hidden>';
                    echo '<input type="text" name="passenger" value="'.$passenger.'" hidden>';
                    echo '<input type="text" name="trip" value="'.$trip.'" hidden>';
                    echo '<input type="text" name="price" value="'.$discountPrice.'" hidden>';
                    echo '<button class="purchase" type="submit" name="purchase">Purchase</button>';
                }
            echo '</form>';
        }
